import { MapContainer, Marker, TileLayer, useMap, useMapEvents, } from "react-leaflet";
import L from "leaflet";
import React, { useState, useEffect } from "react";

const GeoLocation = () => {
    const [location, setLocation] = useState({
        loaded: false,
        coordinates: { lat: "", long: "" },
    });

    const onSuccess = (location) => {
        setLocation({
            loaded: true,
            coordinates: {
                lat: location.coords.latitude,
                long: location.coords.longitude,
            },
        });
    };

    const onError = (error) => {
        setLocation({
            loaded: true,
            error,
        });
    };

    useEffect(() => {
        if (!("geolocation" in navigator)) {
            onError({
                code: 0,
                message: "Geolocation not supported",
            });
        }

        navigator.geolocation.getCurrentPosition(onSuccess, onError);
    }, []);

    return location;
};

const Map = ({ selectedPosition, setSelectedPosition, getName }) => {
    const location = GeoLocation();
    const icon = L.icon({
        iconUrl:
            "data:image/png;base64,",
    });
    //Function to display marker on click
    const Markers = () => {
        const map = useMapEvents({
            click(e) {
                setSelectedPosition([e.latlng.lat, e.latlng.lng]);

                getName();
            },
        });
        return selectedPosition ? (
            <Marker
                key={selectedPosition[0]}
                position={selectedPosition}
                interactive={false}
                icon={icon}
            />
        ) : null;
    };

    const showMyLocation = () => {
        if (location.loaded && !location.error) {
            setSelectedPosition([
                location.coordinates.lat,
                location.coordinates.long,
            ]);
            useMap.current.leafletElement.flyTo(
                [location.coordinates.lat, location.coordinates.long],
                20,
                { animate: true }
            );
        } else {
            alert(location.error.message);
        }
    };
    return (
        <>
            <MapContainer center={selectedPosition} zoom="12">
                <TileLayer
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                />
                <Markers />
            </MapContainer>
        </>
    );
};
export default Map;